CREATE TABLE `OptionExpiry` (  `ID` int(11) NOT NULL AUTO_INCREMENT,  `quote_date` datetime NOT NULL,  `root` varchar(255) NOT NULL,  `expiration` datetime NOT NULL,  `rootOriginal` varchar(255) NOT NULL,  `calendarTTE` double DEFAULT NULL,  PRIMARY KEY (`ID`, `quote_date`, `root`, `expiration`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1;CREATE TABLE `Strike` (  `ID` int(11) NOT NULL AUTO_INCREMENT,  `strike` double NOT NULL,  `option_type` varchar(3) NOT NULL,  PRIMARY KEY (`ID`, `strike`, `option_type`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1;CREATE TABLE `Underlying` (  `ID` int(11) NOT NULL AUTO_INCREMENT,  `OptionExpiryID` int(11) NOT NULL,  `underlying_bid_1545` double DEFAULT NULL,  `underlying_ask_1545` double DEFAULT NULL,  `implied_underlying_price_1545` double DEFAULT NULL,  `active_underlying_price_1545` double DEFAULT NULL,  `underlying_bid_eod` double DEFAULT NULL,  `underlying_ask_eod` double DEFAULT NULL,  PRIMARY KEY (`ID`,`OptionExpiryID`),  KEY `OptionExpiryID` (`OptionExpiryID`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1;CREATE TABLE `OptionGreeks` (  `ID` int(11) NOT NULL AUTO_INCREMENT,  `OptionExpiryID` int(11) NOT NULL,  `StrikeID` int(11) NOT NULL,  `bid_size_1545` int(11) DEFAULT NULL,  `bid_1545` double DEFAULT NULL,  `ask_size_1545` int(11) DEFAULT NULL,  `ask_1545` double DEFAULT NULL,  `implied_volatility_1545` double DEFAULT NULL,  `delta_1545` double DEFAULT NULL,  `gamma_1545` double DEFAULT NULL,  `theta_1545` double DEFAULT NULL,  `vega_1545` double DEFAULT NULL,  `rho_1545` double DEFAULT NULL,  PRIMARY KEY (`ID`, `OptionExpiryID`, `StrikeID`),  KEY `OptionExpiryID` (`OptionExpiryID`),  KEY `StrikeID` (`StrikeID`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1;CREATE TABLE `EoD` (  `ID` int(11) NOT NULL AUTO_INCREMENT,  `OptionExpiryID` int(11) NOT NULL,  `StrikeID` int(11) NOT NULL,  `opn` double DEFAULT NULL,  `high` double DEFAULT NULL,  `low` double DEFAULT NULL,  `clos` double DEFAULT NULL,  `trade_volume` double DEFAULT NULL,  `bid_size_eod` int(11) DEFAULT NULL,  `bid_eod` double DEFAULT NULL,  `ask_size_eod` int(11) DEFAULT NULL,  `ask_eod` double DEFAULT NULL,  `vwap` double DEFAULT NULL,  `open_interest` int(11) DEFAULT NULL,  `delivery_code` int(11) DEFAULT NULL,  PRIMARY KEY (`ID`, `OptionExpiryID`, `StrikeID`),  KEY `OptionExpiryID` (`OptionExpiryID`),  KEY `StrikeID` (`StrikeID`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1;